import { useState, useEffect } from "react";

const LOGO_B64 = "";

const B = {
  darkest:   "#0c293c",
  navy:      "#003f5f",
  teal:      "#2cb1cc",
  cream:     "#f7f3eb",
  navyLight: "#004f78",
  tealDim:   "#1a8599",
  mutedText: "#7eb8c9",
  dimText:   "#b8d4dc",
  border:    "#0a2233",
  borderMid: "#0e3048",
  gold:      "#f5a623",
  red:       "#e05252",
  amber:     "#f0a030",
  green:     "#3cc98a",
};

const F = {
  heading:    "'Raleway', 'Trebuchet MS', sans-serif",
  subheading: "'Raleway', 'Trebuchet MS', sans-serif",
  body:       "'Montserrat', 'Segoe UI', sans-serif",
};

const KEY_THRESHOLDS = [
  { value: 4.5, label: "4.5", note: "Top tier  most consumers consider 4.5+ excellent" },
  { value: 4.0, label: "4.0", note: "Critical floor  many customers filter out below 4.0" },
  { value: 3.5, label: "3.5", note: "Danger zone  significant trust damage below 3.5" },
];

//  Calculations 

function calcReviewsNeeded(currentRating, currentCount, targetRating) {
  const r = parseFloat(currentRating), n = parseInt(currentCount), t = parseFloat(targetRating);
  if (isNaN(r) || isNaN(n) || isNaN(t) || r <= 0 || n <= 0) return null;
  if (t <= r || t > 5) return null;
  const needed = Math.ceil((n * (t - r)) / (5 - t));
  const newRating = ((r * n) + (5 * needed)) / (n + needed);
  return { needed, newTotal: n + needed, newRating };
}

function calcOneStarImpact(currentRating, currentCount, oneStarCount) {
  const r = parseFloat(currentRating), n = parseInt(currentCount), s = parseInt(oneStarCount);
  if (isNaN(r) || isNaN(n) || isNaN(s) || r <= 0 || n <= 0 || s <= 0) return null;
  const newRating = ((r * n) + (1 * s)) / (n + s);
  return { newRating, newTotal: n + s, drop: r - newRating };
}

function calcMaintain(currentRating, currentCount, monthlyOneStar) {
  const r = parseFloat(currentRating), n = parseInt(currentCount), k = parseInt(monthlyOneStar);
  if (isNaN(r) || isNaN(n) || isNaN(k) || r <= 0 || n <= 0 || k <= 0) return null;
  if (r >= 5) return { needed: 0 };
  // To stay flat: m*(5-r) = k*(r-1)    m = k*(r-1)/(5-r)
  const needed = Math.ceil(k * (r - 1) / (5 - r));
  return { needed, ratio: `${k}  1 offset by ${needed}  5`, monthlyOneStar: k };
}

function checkThresholdCrossing(before, after) {
  for (const t of KEY_THRESHOLDS) {
    if (before >= t.value && after < t.value)  return { ...t, direction: "down" };
    if (before < t.value  && after >= t.value) return { ...t, direction: "up"   };
  }
  return null;
}

function nearestThresholdRisk(rating) {
  for (const t of KEY_THRESHOLDS) {
    if (rating >= t.value && rating < t.value + 0.15) return { ...t, gap: (rating - t.value).toFixed(2) };
  }
  return null;
}

//  URL state sync 

function readUrlState() {
  try {
    const p = new URLSearchParams(window.location.search);
    return {
      rating:          p.get("r")   || "",
      count:           p.get("n")   || "",
      tab:             p.get("tab") || "fivestar",
      increment:       parseFloat(p.get("inc")) || 0.1,
      oneStarCount:    p.get("os")  || "",
      monthlyOneStar:  p.get("mos") || "",
      monthlyReviews:  p.get("mr")  || "",
    };
  } catch { return {}; }
}

//  Small components 

function StarDisplay({ rating, size = 18 }) {
  return (
    <span style={{ display: "inline-flex", gap: 2 }}>
      {[1,2,3,4,5].map((s) => {
        const fill = Math.min(Math.max(rating - (s - 1), 0), 1);
        return (
          <span key={s} style={{ position: "relative", display: "inline-block", width: size, height: size }}>
            <span style={{ color: B.borderMid, fontSize: size, lineHeight: 1 }}></span>
            <span style={{ position: "absolute", top: 0, left: 0, overflow: "hidden", width: `${fill * 100}%`, fontSize: size, lineHeight: 1, color: B.gold }}></span>
          </span>
        );
      })}
    </span>
  );
}

function StatBox({ label, value, color, labelColor }) {
  return (
    <div style={{ background: B.navy, border: `1px solid ${B.borderMid}`, borderRadius: 10, padding: "14px", textAlign: "center" }}>
      <div style={{ fontSize: 9, letterSpacing: "0.18em", color: labelColor || B.dimText, textTransform: "uppercase", fontFamily: F.subheading, fontWeight: 600, marginBottom: 6 }}>{label}</div>
      <div style={{ fontSize: 17, fontWeight: 700, color: color || B.cream, fontFamily: F.heading }}>{value}</div>
    </div>
  );
}

function ThresholdAlert({ crossing, isMobile }) {
  if (!crossing) return null;
  const isDown = crossing.direction === "down";
  const bg    = isDown ? "#3a0a0a" : "#0a2a1a";
  const bdr   = isDown ? B.red : B.green;
  const icon  = isDown ? "" : "";
  const msg   = isDown
    ? `This drops you below the ${crossing.label} threshold  ${crossing.note}`
    : `This pushes you above the ${crossing.label} threshold  ${crossing.note}`;
  return (
    <div style={{ background: bg, border: `1px solid ${bdr}55`, borderLeft: `3px solid ${bdr}`, borderRadius: 8, padding: "12px 16px", marginBottom: 16, fontSize: 12, color: B.cream, lineHeight: 1.6 }}>
      {icon} <strong style={{ color: bdr }}>Threshold alert:</strong> {msg}
    </div>
  );
}

function RiskBanner({ risk }) {
  if (!risk) return null;
  return (
    <div style={{ background: "#2a1a00", border: `1px solid ${B.amber}44`, borderLeft: `3px solid ${B.amber}`, borderRadius: 8, padding: "12px 16px", marginBottom: 16, fontSize: 12, color: B.cream, lineHeight: 1.6 }}>
       <strong style={{ color: B.amber }}>Vulnerability alert:</strong> You are only <strong style={{ color: B.amber }}>{risk.gap} points</strong> above the {risk.label} threshold. {risk.note}.
    </div>
  );
}

//  Constants 

const INCREMENTS       = [0.1, 0.2, 0.3, 0.5, 1.0];
const ONE_STAR_PRESETS = [1, 2, 3, 5, 10];
const MONTHLY_PRESETS  = [1, 2, 5, 10, 20];

const labelStyle = {
  fontSize: 9, letterSpacing: "0.18em", color: B.dimText,
  textTransform: "uppercase", fontFamily: F.subheading, fontWeight: 600,
};

const inputStyle = {
  background: B.darkest, border: `1px solid ${B.borderMid}`,
  borderRadius: 8, padding: "10px 14px", color: B.cream,
  fontSize: 22, outline: "none", fontFamily: F.heading,
  fontWeight: 700, width: "100%", boxSizing: "border-box",
  colorScheme: "dark", textAlign: "center",
};

const TABS = [
  { id: "fivestar", label: " 5-Star Impact",   color: B.teal },
  { id: "onestar",  label: " 1-Star Impact",    color: B.red  },
  { id: "maintain", label: " Maintain Rating",  color: B.amber },
];

//  App 

export default function App() {
  const init = readUrlState();

  const [currentRating,  setCurrentRating]  = useState(init.rating);
  const [currentCount,   setCurrentCount]   = useState(init.count);
  const [increment,      setIncrement]      = useState(init.increment);
  const [activeTab,      setActiveTab]      = useState(init.tab);
  const [oneStarCount,   setOneStarCount]   = useState(init.oneStarCount);
  const [monthlyOneStar, setMonthlyOneStar] = useState(init.monthlyOneStar);
  const [monthlyReviews, setMonthlyReviews] = useState(init.monthlyReviews);
  const [copied,         setCopied]         = useState(false);
  const [isMobile,       setIsMobile]       = useState(window.innerWidth < 640);

  useEffect(() => {
    const handler = () => setIsMobile(window.innerWidth < 640);
    window.addEventListener("resize", handler);
    return () => window.removeEventListener("resize", handler);
  }, []);

  // Sync state to URL
  useEffect(() => {
    const params = new URLSearchParams();
    if (currentRating) params.set("r",   currentRating);
    if (currentCount)  params.set("n",   currentCount);
    if (activeTab !== "fivestar") params.set("tab", activeTab);
    if (increment !== 0.1) params.set("inc", increment);
    if (oneStarCount)  params.set("os",  oneStarCount);
    if (monthlyOneStar) params.set("mos", monthlyOneStar);
    if (monthlyReviews) params.set("mr",  monthlyReviews);
    const qs = params.toString();
    window.history.replaceState(null, "", qs ? `?${qs}` : window.location.pathname);
  }, [currentRating, currentCount, increment, activeTab, oneStarCount, monthlyOneStar, monthlyReviews]);

  const copyLink = () => {
    navigator.clipboard.writeText(window.location.href).then(() => {
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    });
  };

  const r = parseFloat(currentRating);
  const n = parseInt(currentCount);
  const validBase = !isNaN(r) && !isNaN(n) && r > 0 && r <= 5 && n > 0;

  const target       = validBase ? Math.min(r + increment, 5.0) : null;
  const result       = calcReviewsNeeded(currentRating, currentCount, target);
  const oneStarResult = calcOneStarImpact(currentRating, currentCount, oneStarCount);
  const maintainResult = calcMaintain(currentRating, currentCount, monthlyOneStar);

  const fiveStarCrossing = result      ? checkThresholdCrossing(r, result.newRating)       : null;
  const oneStarCrossing  = oneStarResult ? checkThresholdCrossing(r, oneStarResult.newRating) : null;
  const riskAtRest       = validBase   ? nearestThresholdRisk(r) : null;

  const recoveryResult = oneStarResult
    ? calcReviewsNeeded(String(oneStarResult.newRating), String(oneStarResult.newTotal), String(r))
    : null;

  const monthlyNum     = parseInt(monthlyReviews);
  const recoveryMonths = recoveryResult && !isNaN(monthlyNum) && monthlyNum > 0
    ? (recoveryResult.needed / monthlyNum).toFixed(1)
    : null;

  // Styles
  const tabBtn = (id) => ({
    flex: 1,
    background: activeTab === id ? TABS.find(t => t.id === id).color : "none",
    border: `1px solid ${activeTab === id ? TABS.find(t => t.id === id).color : B.borderMid}`,
    borderRadius: 10,
    padding: isMobile ? "10px 6px" : "12px 16px",
    color: activeTab === id ? (id === "fivestar" ? B.darkest : "#fff") : B.mutedText,
    fontSize: isMobile ? 11 : 13,
    cursor: "pointer",
    fontFamily: F.subheading,
    fontWeight: 700,
    textAlign: "center",
    transition: "all 0.2s",
  });

  const grid2 = { display: "grid", gridTemplateColumns: isMobile ? "1fr" : "1fr 1fr", gap: 24 };
  const grid3 = { display: "grid", gridTemplateColumns: isMobile ? "1fr" : "repeat(3, 1fr)", gap: 10 };
  const beforeAfterGrid = {
    display: "grid",
    gridTemplateColumns: isMobile ? "1fr" : "1fr 40px 1fr",
    alignItems: "center",
    gap: 12,
    marginBottom: 24,
  };

  const card = (borderColor) => ({
    background: B.navy,
    border: `1px solid ${borderColor || B.borderMid}`,
    borderRadius: 12,
    padding: "20px",
    textAlign: "center",
  });

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@400;500;600&family=Raleway:wght@400;600;700&display=swap');
        * { box-sizing: border-box; margin: 0; padding: 0; }
        input::-webkit-inner-spin-button, input::-webkit-outer-spin-button { opacity: 0.3; }
        input:focus { border-color: ${B.teal} !important; box-shadow: 0 0 0 2px ${B.teal}22; outline: none; }
        button { transition: all 0.15s; }
        button:hover { opacity: 0.85; }
        body { overflow-x: hidden; }
      `}</style>

      <div style={{ minHeight: "100vh", background: `linear-gradient(160deg, ${B.darkest} 0%, #091e2d 55%, ${B.darkest} 100%)`, fontFamily: F.body, color: B.cream }}>

        {/*  Header  */}
        <div style={{ background: `linear-gradient(90deg, ${B.navy} 0%, #004068 100%)`, borderBottom: `1px solid ${B.border}`, padding: isMobile ? "24px 20px 20px" : "36px 52px 30px", position: "relative" }}>
          <div style={{ position: "absolute", top: 0, left: 0, right: 0, height: 3, background: `linear-gradient(90deg, ${B.teal}, ${B.tealDim}, ${B.teal})` }} />

          <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between", gap: 16 }}>
            <div style={{ display: "flex", alignItems: "center", gap: 16 }}>
              <img
                src={`data:image/jpeg;base64,${LOGO_B64}`}
                alt="Logo"
                style={{ height: isMobile ? 40 : 52, width: isMobile ? 40 : 52, objectFit: "contain", filter: "invert(1)", flexShrink: 0 }}
              />
              <div>
                <div style={{ fontSize: 10, letterSpacing: "0.3em", color: B.teal, textTransform: "uppercase", fontFamily: F.subheading, fontWeight: 600, marginBottom: 4 }}>
                  Reputation Management
                </div>
                <h1 style={{ fontSize: isMobile ? 22 : 32, fontWeight: "normal", color: B.cream, fontFamily: F.heading, lineHeight: 1.1 }}>
                  Google Review <span style={{ color: B.teal }}>Score Calculator</span>
                </h1>
              </div>
            </div>

            {/* Share button */}
            <button
              onClick={copyLink}
              style={{
                background: copied ? B.green : "none",
                border: `1px solid ${copied ? B.green : B.borderMid}`,
                borderRadius: 8, padding: "8px 14px",
                color: copied ? "#fff" : B.mutedText,
                fontSize: 12, cursor: "pointer",
                fontFamily: F.subheading, fontWeight: 700,
                flexShrink: 0, whiteSpace: "nowrap",
              }}
            >
              {copied ? " Copied!" : " Share"}
            </button>
          </div>

          <p style={{ margin: "10px 0 0", color: B.mutedText, fontSize: 12, paddingLeft: isMobile ? 0 : 68 }}>
            See how reviews move your rating  and what it takes to protect it
          </p>
        </div>

        {/*  Main  */}
        <div style={{ padding: isMobile ? "20px 16px 48px" : "40px 52px 60px", maxWidth: 820, margin: "0 auto" }}>

          {/* Input card */}
          <div style={{ background: `linear-gradient(135deg, ${B.navy}, ${B.navyLight})`, border: `1px solid ${B.borderMid}`, borderRadius: 16, padding: isMobile ? "20px 16px" : "32px 36px", marginBottom: 20 }}>

            <div style={{ ...grid2, marginBottom: 24 }}>
              <label style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                <span style={labelStyle}>Current Google Rating</span>
                <input type="number" value={currentRating} min="1.0" max="5.0" step="0.1"
                  placeholder="e.g. 4.2"
                  onChange={(e) => setCurrentRating(e.target.value)}
                  style={inputStyle} />
              </label>
              <label style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                <span style={labelStyle}>Number of Reviews</span>
                <input type="number" value={currentCount} min="1" step="1"
                  placeholder="e.g. 200"
                  onChange={(e) => setCurrentCount(e.target.value)}
                  style={inputStyle} />
              </label>
            </div>

            {/* Star preview + threshold risk */}
            {validBase && (
              <>
                <div style={{ display: "flex", justifyContent: "center", marginBottom: 12 }}>
                  <StarDisplay rating={r} size={28} />
                </div>
                {riskAtRest && activeTab !== "onestar" && <RiskBanner risk={riskAtRest} />}
              </>
            )}

            {/* Tabs */}
            <div style={{ display: "flex", gap: 8, marginBottom: 24 }}>
              {TABS.map((t) => (
                <button key={t.id} onClick={() => setActiveTab(t.id)} style={tabBtn(t.id)}>
                  {t.label}
                </button>
              ))}
            </div>

            {/*  5-Star tab controls  */}
            {activeTab === "fivestar" && (
              <div>
                <div style={{ ...labelStyle, marginBottom: 12 }}>I want to increase my rating by</div>
                <div style={{ display: "flex", flexWrap: "wrap", gap: 8, marginBottom: 20 }}>
                  {INCREMENTS.map((inc) => (
                    <button key={inc} onClick={() => setIncrement(inc)} style={{
                      background: increment === inc ? B.teal : "none",
                      border: `1px solid ${increment === inc ? B.teal : B.borderMid}`,
                      borderRadius: 8, padding: "9px 22px",
                      color: increment === inc ? B.darkest : B.mutedText,
                      fontSize: 14, cursor: "pointer",
                      fontFamily: F.subheading, fontWeight: 700,
                    }}>
                      +{inc.toFixed(1)}
                    </button>
                  ))}
                </div>
                <div style={{ ...grid2 }}>
                  <label style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                    <span style={labelStyle}>Monthly review volume (optional)</span>
                    <input type="number" value={monthlyReviews} min="1" step="1"
                      placeholder="e.g. 15"
                      onChange={(e) => setMonthlyReviews(e.target.value)}
                      style={{ ...inputStyle, fontSize: 18 }} />
                  </label>
                </div>
              </div>
            )}

            {/*  1-Star tab controls  */}
            {activeTab === "onestar" && (
              <div>
                <div style={{ ...labelStyle, marginBottom: 12 }}>How many 1-star reviews do you receive?</div>
                <div style={{ display: "flex", flexWrap: "wrap", gap: 8, marginBottom: 16 }}>
                  {ONE_STAR_PRESETS.map((n) => (
                    <button key={n} onClick={() => setOneStarCount(String(n))} style={{
                      background: oneStarCount === String(n) ? B.red : "none",
                      border: `1px solid ${oneStarCount === String(n) ? B.red : B.borderMid}`,
                      borderRadius: 8, padding: "9px 22px",
                      color: oneStarCount === String(n) ? "#fff" : B.mutedText,
                      fontSize: 14, cursor: "pointer",
                      fontFamily: F.subheading, fontWeight: 700,
                    }}>
                      {n}
                    </button>
                  ))}
                </div>
                <div style={{ ...grid2 }}>
                  <label style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                    <span style={{ ...labelStyle, color: B.red }}>Custom 1-star count</span>
                    <input type="number" value={oneStarCount} min="1" step="1"
                      placeholder="e.g. 7"
                      onChange={(e) => setOneStarCount(e.target.value)}
                      style={{ ...inputStyle, borderColor: oneStarCount ? B.red : B.borderMid, fontSize: 18 }}
                    />
                  </label>
                  <label style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                    <span style={labelStyle}>Monthly 5-star volume (for recovery time)</span>
                    <input type="number" value={monthlyReviews} min="1" step="1"
                      placeholder="e.g. 15"
                      onChange={(e) => setMonthlyReviews(e.target.value)}
                      style={{ ...inputStyle, fontSize: 18 }} />
                  </label>
                </div>
              </div>
            )}

            {/*  Maintain tab controls  */}
            {activeTab === "maintain" && (
              <div>
                <div style={{ ...labelStyle, marginBottom: 12 }}>Expected 1-star reviews per month</div>
                <div style={{ display: "flex", flexWrap: "wrap", gap: 8, marginBottom: 16 }}>
                  {MONTHLY_PRESETS.map((n) => (
                    <button key={n} onClick={() => setMonthlyOneStar(String(n))} style={{
                      background: monthlyOneStar === String(n) ? B.amber : "none",
                      border: `1px solid ${monthlyOneStar === String(n) ? B.amber : B.borderMid}`,
                      borderRadius: 8, padding: "9px 22px",
                      color: monthlyOneStar === String(n) ? B.darkest : B.mutedText,
                      fontSize: 14, cursor: "pointer",
                      fontFamily: F.subheading, fontWeight: 700,
                    }}>
                      {n}/mo
                    </button>
                  ))}
                </div>
                <label style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                  <span style={{ ...labelStyle, color: B.amber }}>Or enter a custom monthly amount</span>
                  <input type="number" value={monthlyOneStar} min="1" step="1"
                    placeholder="e.g. 3"
                    onChange={(e) => setMonthlyOneStar(e.target.value)}
                    style={{ ...inputStyle, borderColor: monthlyOneStar ? B.amber : B.borderMid }}
                  />
                </label>
              </div>
            )}
          </div>

          {/*  5-STAR RESULT  */}
          {activeTab === "fivestar" && result && target && (
            <div style={{ background: `linear-gradient(135deg, ${B.darkest}, #0a2535)`, border: `1px solid ${B.borderMid}`, borderTop: `2px solid ${B.teal}`, borderRadius: 16, padding: isMobile ? "24px 16px" : "36px 36px 28px" }}>

              <ThresholdAlert crossing={fiveStarCrossing} />

              <div style={{ textAlign: "center", marginBottom: 32 }}>
                <div style={{ ...labelStyle, marginBottom: 12 }}>5-Star Reviews Needed</div>
                <div style={{ fontSize: isMobile ? 72 : 96, fontWeight: 700, color: B.teal, fontFamily: F.heading, lineHeight: 1, letterSpacing: "-0.02em" }}>
                  {result.needed.toLocaleString()}
                </div>
                <div style={{ fontSize: 13, color: B.mutedText, marginTop: 12 }}>
                  to move from <strong style={{ color: B.cream }}>{r.toFixed(1)} </strong> to <strong style={{ color: B.gold }}>{target.toFixed(1)} </strong>
                </div>
                {monthlyReviews && !isNaN(monthlyNum) && monthlyNum > 0 && (
                  <div style={{ marginTop: 12, display: "inline-block", background: `${B.teal}18`, border: `1px solid ${B.teal}44`, borderRadius: 8, padding: "8px 16px", fontSize: 12, color: B.teal }}>
                     At {monthlyNum} reviews/month  approximately <strong>{(result.needed / monthlyNum).toFixed(1)} months</strong> to reach your target
                  </div>
                )}
              </div>

              <div style={beforeAfterGrid}>
                <div style={card()}>
                  <div style={{ ...labelStyle, marginBottom: 8 }}>Today</div>
                  <div style={{ fontSize: 40, fontWeight: 700, fontFamily: F.heading, color: B.cream, marginBottom: 8 }}>{r.toFixed(1)}</div>
                  <StarDisplay rating={r} size={15} />
                  <div style={{ fontSize: 11, color: B.dimText, marginTop: 8 }}>{n.toLocaleString()} reviews</div>
                </div>
                {!isMobile && <div style={{ textAlign: "center", color: B.teal, fontSize: 22, fontWeight: 700 }}></div>}
                <div style={card(B.teal)}>
                  <div style={{ ...labelStyle, marginBottom: 8, color: B.teal }}>After</div>
                  <div style={{ fontSize: 40, fontWeight: 700, fontFamily: F.heading, color: B.gold, marginBottom: 8 }}>{result.newRating.toFixed(2)}</div>
                  <StarDisplay rating={result.newRating} size={15} />
                  <div style={{ fontSize: 11, color: B.dimText, marginTop: 8 }}>{result.newTotal.toLocaleString()} reviews</div>
                </div>
              </div>

              <div style={{ ...grid3, marginBottom: 16 }}>
                <StatBox label="Reviews to Collect" value={result.needed.toLocaleString()} color={B.teal} />
                <StatBox label="Total After"         value={result.newTotal.toLocaleString()} />
                <StatBox label="Exact New Rating"    value={result.newRating.toFixed(2) + " "} color={B.gold} />
              </div>

              {/* Threshold progress */}
              <div style={{ background: B.navy, border: `1px solid ${B.borderMid}`, borderRadius: 10, padding: "16px", marginBottom: 16 }}>
                <div style={{ ...labelStyle, marginBottom: 12 }}>Key Thresholds</div>
                {KEY_THRESHOLDS.map((t) => {
                  const achieved = result.newRating >= t.value;
                  const alreadyHad = r >= t.value;
                  return (
                    <div key={t.value} style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 8 }}>
                      <div style={{ width: 14, height: 14, borderRadius: "50%", background: achieved ? B.teal : (alreadyHad ? B.gold : B.borderMid), flexShrink: 0 }} />
                      <span style={{ fontSize: 12, color: achieved ? B.teal : (alreadyHad ? B.gold : B.mutedText), fontWeight: achieved || alreadyHad ? 600 : 400 }}>
                        {t.label}
                      </span>
                      <span style={{ fontSize: 11, color: B.dimText }}> {t.note}</span>
                    </div>
                  );
                })}
              </div>

              <p style={{ fontSize: 11, color: B.dimText, textAlign: "center", lineHeight: 1.7 }}>
                Google may display ratings slightly differently due to their own rounding and spam-filtering. These figures represent the mathematical average.
              </p>
            </div>
          )}

          {/*  1-STAR RESULT  */}
          {activeTab === "onestar" && oneStarResult && (
            <div style={{ background: `linear-gradient(135deg, ${B.darkest}, #2a0a0a)`, border: `1px solid ${B.borderMid}`, borderTop: `2px solid ${B.red}`, borderRadius: 16, padding: isMobile ? "24px 16px" : "36px 36px 28px" }}>

              <ThresholdAlert crossing={oneStarCrossing} />
              {!oneStarCrossing && riskAtRest && <RiskBanner risk={riskAtRest} />}

              <div style={{ textAlign: "center", marginBottom: 32 }}>
                <div style={{ ...labelStyle, marginBottom: 12, color: B.red }}>Rating Drop</div>
                <div style={{ fontSize: isMobile ? 72 : 96, fontWeight: 700, color: B.red, fontFamily: F.heading, lineHeight: 1, letterSpacing: "-0.02em" }}>
                  {oneStarResult.drop.toFixed(2)}
                </div>
                <div style={{ fontSize: 13, color: B.mutedText, marginTop: 12 }}>
                  {parseInt(oneStarCount).toLocaleString()} one-star review{parseInt(oneStarCount) !== 1 ? "s" : ""} drops your rating from <strong style={{ color: B.cream }}>{r.toFixed(1)} </strong> to <strong style={{ color: B.red }}>{oneStarResult.newRating.toFixed(2)} </strong>
                </div>
              </div>

              <div style={beforeAfterGrid}>
                <div style={card()}>
                  <div style={{ ...labelStyle, marginBottom: 8 }}>Today</div>
                  <div style={{ fontSize: 40, fontWeight: 700, fontFamily: F.heading, color: B.cream, marginBottom: 8 }}>{r.toFixed(1)}</div>
                  <StarDisplay rating={r} size={15} />
                  <div style={{ fontSize: 11, color: B.dimText, marginTop: 8 }}>{n.toLocaleString()} reviews</div>
                </div>
                {!isMobile && <div style={{ textAlign: "center", color: B.red, fontSize: 22, fontWeight: 700 }}></div>}
                <div style={card(`${B.red}55`)}>
                  <div style={{ ...labelStyle, marginBottom: 8, color: B.red }}>After {parseInt(oneStarCount)}  1</div>
                  <div style={{ fontSize: 40, fontWeight: 700, fontFamily: F.heading, color: B.red, marginBottom: 8 }}>{oneStarResult.newRating.toFixed(2)}</div>
                  <StarDisplay rating={oneStarResult.newRating} size={15} />
                  <div style={{ fontSize: 11, color: B.dimText, marginTop: 8 }}>{oneStarResult.newTotal.toLocaleString()} reviews</div>
                </div>
              </div>

              <div style={{ ...grid3, marginBottom: 16 }}>
                <StatBox label="1-Star Reviews Added" value={parseInt(oneStarCount).toLocaleString()} color={B.red} />
                <StatBox label="Total After"          value={oneStarResult.newTotal.toLocaleString()} />
                <StatBox label="New Rating"           value={oneStarResult.newRating.toFixed(2) + " "} color={B.red} />
              </div>

              {/* Recovery plan */}
              {recoveryResult && (
                <div style={{ background: `${B.teal}12`, border: `1px solid ${B.teal}33`, borderRadius: 10, padding: "16px 20px", marginBottom: 16 }}>
                  <div style={{ ...labelStyle, color: B.teal, marginBottom: 10 }}>Recovery Plan</div>
                  <div style={{ fontSize: 13, color: B.mutedText, lineHeight: 1.9 }}>
                    To get back to <strong style={{ color: B.cream }}>{r.toFixed(1)} </strong> you need{" "}
                    <strong style={{ color: B.teal, fontSize: 16 }}>{recoveryResult.needed.toLocaleString()}  5-star reviews</strong>
                  </div>
                  {recoveryMonths && (
                    <div style={{ marginTop: 8, fontSize: 13, color: B.mutedText }}>
                      At {monthlyNum} reviews/month: <strong style={{ color: B.teal }}>~{recoveryMonths} months</strong> to full recovery
                    </div>
                  )}
                </div>
              )}

              <p style={{ fontSize: 11, color: B.dimText, textAlign: "center", lineHeight: 1.7 }}>
                Google may display ratings slightly differently due to their own rounding and spam-filtering. These figures represent the mathematical average.
              </p>
            </div>
          )}

          {/*  MAINTAIN RESULT  */}
          {activeTab === "maintain" && maintainResult && validBase && (
            <div style={{ background: `linear-gradient(135deg, ${B.darkest}, #1a1500)`, border: `1px solid ${B.borderMid}`, borderTop: `2px solid ${B.amber}`, borderRadius: 16, padding: isMobile ? "24px 16px" : "36px 36px 28px" }}>

              {riskAtRest && <RiskBanner risk={riskAtRest} />}

              <div style={{ textAlign: "center", marginBottom: 32 }}>
                <div style={{ ...labelStyle, marginBottom: 12, color: B.amber }}>5-Star Reviews Needed Per Month</div>
                <div style={{ fontSize: isMobile ? 72 : 96, fontWeight: 700, color: B.amber, fontFamily: F.heading, lineHeight: 1, letterSpacing: "-0.02em" }}>
                  {maintainResult.needed.toLocaleString()}
                </div>
                <div style={{ fontSize: 13, color: B.mutedText, marginTop: 12 }}>
                  to keep your <strong style={{ color: B.cream }}>{r.toFixed(1)} </strong> rating stable against <strong style={{ color: B.red }}>{parseInt(monthlyOneStar)}  1-star reviews/month</strong>
                </div>
              </div>

              {/* Ratio card */}
              <div style={{ background: B.navy, border: `1px solid ${B.amber}44`, borderRadius: 12, padding: "24px", textAlign: "center", marginBottom: 20 }}>
                <div style={{ ...labelStyle, marginBottom: 16, color: B.amber }}>Monthly Review Ratio to Stay Flat</div>
                <div style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: isMobile ? 12 : 24, flexWrap: "wrap" }}>
                  <div style={{ textAlign: "center" }}>
                    <div style={{ fontSize: isMobile ? 36 : 48, fontWeight: 700, color: B.red, fontFamily: F.heading }}>{parseInt(monthlyOneStar)}</div>
                    <div style={{ fontSize: 11, color: B.dimText }}>1-star / month</div>
                  </div>
                  <div style={{ fontSize: 24, color: B.dimText }}>:</div>
                  <div style={{ textAlign: "center" }}>
                    <div style={{ fontSize: isMobile ? 36 : 48, fontWeight: 700, color: B.amber, fontFamily: F.heading }}>{maintainResult.needed}</div>
                    <div style={{ fontSize: 11, color: B.dimText }}>5-star / month</div>
                  </div>
                </div>
              </div>

              {/* What if you want to grow? */}
              {result && target && (
                <div style={{ background: `${B.teal}10`, border: `1px solid ${B.teal}33`, borderRadius: 10, padding: "16px 20px", marginBottom: 16 }}>
                  <div style={{ ...labelStyle, color: B.teal, marginBottom: 8 }}>Growth on Top</div>
                  <div style={{ fontSize: 13, color: B.mutedText, lineHeight: 1.9 }}>
                    To <em>also</em> grow to <strong style={{ color: B.gold }}>{target.toFixed(1)} </strong> while absorbing {parseInt(monthlyOneStar)} 1-stars/month, you need{" "}
                    <strong style={{ color: B.teal }}>{(maintainResult.needed + result.needed).toLocaleString()} total 5-star reviews</strong>
                    {monthlyReviews && !isNaN(monthlyNum) && monthlyNum > 0 &&
                      <span>  roughly <strong style={{ color: B.teal }}>{((maintainResult.needed + result.needed) / monthlyNum).toFixed(1)} months</strong> at your current pace</span>
                    }
                  </div>
                </div>
              )}

              {/* Threshold context */}
              <div style={{ background: B.navy, border: `1px solid ${B.borderMid}`, borderRadius: 10, padding: "16px", marginBottom: 16 }}>
                <div style={{ ...labelStyle, marginBottom: 12 }}>Threshold Status</div>
                {KEY_THRESHOLDS.map((t) => (
                  <div key={t.value} style={{ display: "flex", alignItems: "center", gap: 10, marginBottom: 8 }}>
                    <div style={{ width: 14, height: 14, borderRadius: "50%", background: r >= t.value ? B.amber : B.borderMid, flexShrink: 0 }} />
                    <span style={{ fontSize: 12, color: r >= t.value ? B.amber : B.mutedText, fontWeight: r >= t.value ? 600 : 400 }}>{t.label}</span>
                    <span style={{ fontSize: 11, color: B.dimText }}> {t.note}</span>
                  </div>
                ))}
              </div>

              <p style={{ fontSize: 11, color: B.dimText, textAlign: "center", lineHeight: 1.7 }}>
                Google may display ratings slightly differently due to their own rounding and spam-filtering. These figures represent the mathematical average.
              </p>
            </div>
          )}

          {currentRating && r >= 5 && (
            <div style={{ textAlign: "center", padding: 32, color: B.gold, fontSize: 15 }}>
               You have a perfect 5.0 rating!
            </div>
          )}

        </div>
      </div>
    </>
  );
}
