//  AI Analysis 
async function runAIAnalysis(location, trend) {
  const allReviews = trend.flatMap(m=>
    (m.rv||[]).map(r=>`[${monthLabel(m.m)}  ${r.r}] ${r.t}`)
  ).slice(-120);
  const recentTrend = trend.slice(-6).map(m=>`${monthLabel(m.m)}: ${m.avg} (${m.count||m.c} reviews)`).join(", ");
  const prompt = `You are a reputation management expert for Birchstone Residential, a multifamily property management company. Analyze Google Reviews for: "${location}"

Recent rating trend (last 6 months): ${recentTrend}

Reviews:
---
${allReviews.join("\n")}
---

Respond ONLY with valid JSON (no markdown) in this exact structure:
{"summary":"2-3 sentence executive summary","healthScore":<1-10>,"trend":"improving"|"stable"|"declining","positiveThemes":[{"theme":"name","description":"one sentence","frequency":"high|medium|low"}],"negativeThemes":[{"theme":"name","description":"one sentence","frequency":"high|medium|low"}],"quickWins":["action 1","action 2","action 3"],"responseStrategy":"2 sentences on responding to negative reviews","biggestOpportunity":"single most impactful improvement"}`;

  const resp = await fetch("https://api.anthropic.com/v1/messages",{
    method:"POST",headers:{"Content-Type":"application/json"},
    body:JSON.stringify({model:"claude-sonnet-4-20250514",max_tokens:1000,messages:[{role:"user",content:prompt}]}),
  });
  const data = await resp.json();
  const raw = data.content?.find(b=>b.type==="text")?.text||"";
  return JSON.parse(raw.replace(/```json|```/g,"").trim());
}

//  Upload Panel 
function parseCSVLine(line){const r=[];let cur="",inQ=false;for(const c of line){if(c==='"')inQ=!inQ;else if(c===','&&!inQ){r.push(cur);cur="";}else cur+=c;}r.push(cur);return r;}

function UploadPanel({onData,onClose}){
  const[file,setFile]=useState(null);
  const[rows,setRows]=useState(null);
  const[headers,setHeaders]=useState([]);
  const[cols,setCols]=useState({date:"",location:"",rating:"",review:""});
  const[err,setErr]=useState("");
  const[mode,setMode]=useState("append");

  const handleFile=(f)=>{
    if(!f)return;
    const reader=new FileReader();
    reader.onload=e=>{
      const lines=e.target.result.trim().split(/\r?\n/);
      if(lines.length<2){setErr("File too small");return;}
      const hdrs=parseCSVLine(lines[0]).map(h=>h.trim().replace(/^"|"$/g,"").toLowerCase());
      const parsed=[];
      for(let i=1;i<lines.length;i++){
        const vals=parseCSVLine(lines[i]);
        if(vals.length<2)continue;
        const row={};
        hdrs.forEach((h,idx)=>{row[h]=(vals[idx]||"").trim().replace(/^"|"$/g,"");});
        parsed.push(row);
      }
      setHeaders(hdrs);setRows(parsed);setFile(f);
      // Auto-detect
      const find=(...terms)=>hdrs.find(h=>terms.some(t=>h.includes(t)))||"";
      setCols({
        date:find("date","month","time"),
        location:find("location","property","site","name","store"),
        rating:find("rating","star","score"),
        review:find("review","comment","text","feedback","note"),
      });
      setErr("");
    };
    reader.readAsText(f);
  };

  const monthKey=(str)=>{
    if(!str)return null;
    const s=str.trim();
    let m=s.match(/^(\d{4})-(\d{2})/);if(m)return`${m[1]}-${m[2]}`;
    m=s.match(/^(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{2,4})$/);if(m){const y=+m[3]<100?2000+(+m[3]):+m[3];return`${y}-${String(m[1]).padStart(2,"0")}`;}
    m=s.match(/^([A-Za-z]+)\s+(\d{4})$/);if(m){const d=new Date(`${m[1]} 1, ${m[2]}`);if(!isNaN(d))return`${d.getFullYear()}-${String(d.getMonth()+1).padStart(2,"0")}`;}
    return null;
  };

  const doImport=()=>{
    if(!rows||!cols.date||!cols.location||!cols.rating){setErr("Please map Date, Location, and Rating columns.");return;}
    const byLocMonth={};
    rows.forEach(row=>{
      const loc=row[cols.location];const mk=monthKey(row[cols.date]);const rat=parseInt(row[cols.rating]);
      if(!loc||!mk||isNaN(rat)||rat<1||rat>5)return;
      if(!byLocMonth[loc])byLocMonth[loc]={};
      if(!byLocMonth[loc][mk])byLocMonth[loc][mk]={ratings:[],reviews:[]};
      byLocMonth[loc][mk].ratings.push(rat);
      const txt=(row[cols.review]||"").slice(0,250);
      if(txt&&byLocMonth[loc][mk].reviews.length<6)byLocMonth[loc][mk].reviews.push({r:rat,t:txt});
    });
    const newData={};
    Object.keys(byLocMonth).sort().forEach(loc=>{
      newData[loc]=Object.keys(byLocMonth[loc]).sort().map(mk=>{
        const{ratings,reviews}=byLocMonth[loc][mk];
        return{m:mk,avg:+(ratings.reduce((a,b)=>a+b,0)/ratings.length).toFixed(2),c:ratings.length,rv:reviews};
      });
    });
    onData(newData,mode);
  };

  const sel=(key,label,color)=>(
    <label style={{display:"flex",flexDirection:"column",gap:5}}>
      <span style={{...lbl,color:color||B.dimText}}>{label}</span>
      <select value={cols[key]} onChange={e=>setCols(c=>({...c,[key]:e.target.value}))}
        style={{background:B.darkest,border:`1px solid ${cols[key]?color||B.teal:B.red}`,borderRadius:7,padding:"8px 10px",color:B.cream,fontSize:12,fontFamily:F.body,outline:"none",colorScheme:"dark"}}>
        <option value=""> select </option>
        {headers.map(h=><option key={h} value={h}>{h}</option>)}
      </select>
    </label>
  );

  return(
    <div style={{background:B.navy,border:`1px solid ${B.teal}55`,borderRadius:14,padding:"20px",marginBottom:20}}>
      <div style={{display:"flex",justifyContent:"space-between",alignItems:"center",marginBottom:16}}>
        <div style={{fontSize:13,fontWeight:700,color:B.teal,fontFamily:F.heading}}> Add Monthly Data</div>
        <button onClick={onClose} style={{background:"none",border:"none",color:B.dimText,fontSize:18,cursor:"pointer"}}></button>
      </div>
      <div style={{fontSize:11,color:B.mutedText,marginBottom:14,lineHeight:1.7}}>
        Upload a CSV with columns for <strong style={{color:B.teal}}>Date</strong>, <strong style={{color:B.teal}}>Location</strong>, <strong style={{color:B.teal}}>Star Rating</strong>, and optionally Review Text. New months will be added to the existing trend.
      </div>
      {!rows?(
        <div onClick={()=>document.getElementById("csv-up").click()}
          style={{border:`2px dashed ${B.borderMid}`,borderRadius:10,padding:"28px",textAlign:"center",cursor:"pointer"}}>
          <div style={{fontSize:28,marginBottom:8}}></div>
          <div style={{fontSize:13,color:B.cream,fontFamily:F.heading,marginBottom:4}}>Click to upload CSV</div>
          <div style={{fontSize:10,color:B.mutedText}}>Supports any CSV with date, location, and rating columns</div>
          <input id="csv-up" type="file" accept=".csv" style={{display:"none"}} onChange={e=>handleFile(e.target.files[0])}/>
        </div>
      ):(
        <div>
          <div style={{background:`${B.green}12`,border:`1px solid ${B.green}33`,borderRadius:8,padding:"8px 12px",marginBottom:14,fontSize:11,color:B.green}}>
             Loaded <strong>{rows.length} rows</strong>  map your columns below
          </div>
          <div style={{display:"grid",gridTemplateColumns:"1fr 1fr",gap:12,marginBottom:14}}>
            {sel("date","Date / Month *",B.teal)}
            {sel("location","Location / Property *",B.purple)}
            {sel("rating","Star Rating *",B.gold)}
            <label style={{display:"flex",flexDirection:"column",gap:5}}>
              <span style={{...lbl}}>Review Text (optional)</span>
              <select value={cols.review} onChange={e=>setCols(c=>({...c,review:e.target.value}))}
                style={{background:B.darkest,border:`1px solid ${B.borderMid}`,borderRadius:7,padding:"8px 10px",color:B.cream,fontSize:12,fontFamily:F.body,outline:"none",colorScheme:"dark"}}>
                <option value=""> none </option>
                {headers.map(h=><option key={h} value={h}>{h}</option>)}
              </select>
            </label>
          </div>
          <div style={{display:"flex",gap:8,marginBottom:14}}>
            {[["append"," Add to existing"],["replace"," Replace all data"]].map(([v,l])=>(
              <button key={v} onClick={()=>setMode(v)} style={{flex:1,padding:"8px",borderRadius:7,border:`1px solid ${mode===v?B.teal:B.borderMid}`,background:mode===v?`${B.teal}18`:"none",color:mode===v?B.teal:B.mutedText,fontSize:11,fontFamily:F.heading,fontWeight:700,cursor:"pointer"}}>{l}</button>
            ))}
          </div>
          <div style={{display:"flex",gap:10}}>
            <button onClick={()=>{setRows(null);setFile(null);}} style={{flex:1,padding:"9px",borderRadius:8,border:`1px solid ${B.borderMid}`,background:"none",color:B.mutedText,fontSize:12,fontFamily:F.heading,cursor:"pointer"}}> Back</button>
            <button onClick={doImport} style={{flex:2,padding:"9px",borderRadius:8,border:"none",background:`linear-gradient(135deg,${B.teal},${B.tealDim})`,color:B.darkest,fontSize:12,fontFamily:F.heading,fontWeight:700,cursor:"pointer"}}>Import {rows.length} Rows </button>
          </div>
        </div>
      )}
      {err&&<div style={{marginTop:10,color:B.red,fontSize:11}}>{err}</div>}
    </div>
  );
}

//  Location Dashboard 
function LocationDashboard({location,trend}){
  const[analysis,setAnalysis]=useState(null);
  const[loading,setLoading]=useState(false);
  const[err,setErr]=useState("");
  const[done,setDone]=useState(false);

  const latest=trend[trend.length-1];
  const prev=trend[trend.length-2];
  const delta=latest&&prev?latest.avg-prev.avg:null;
  const totalReviews=trend.reduce((s,m)=>s+(m.count||m.c),0);
  const overallAvg=trend.reduce((s,m)=>s+m.avg*(m.count||m.c),0)/totalReviews;
  const trendDir=delta===null?null:delta>0.05?"up":delta<-0.05?"down":"flat";
  const trendColor={up:B.green,flat:B.amber,down:B.red}[trendDir]||B.dimText;
  const trendIcon={up:"",flat:"",down:""}[trendDir]||"";
  const freqColor={high:B.red,medium:B.amber,low:B.mutedText};

  // normalize trend entries (handle both key formats)
  const normTrend=trend.map(m=>({...m,month:m.m||m.month,avg:m.avg||m.a,count:m.count||m.c||0,reviews:m.rv||m.reviews||[],label:monthLabel(m.m||m.month)}));

  const analyze=async()=>{
    setLoading(true);setErr("");setAnalysis(null);
    try{const r=await runAIAnalysis(location,normTrend);setAnalysis(r);setDone(true);}
    catch(e){setErr("Analysis failed  try again.");}
    finally{setLoading(false);}
  };

  return(<div>
    {/* Stats */}
    <div style={{display:"grid",gridTemplateColumns:"repeat(4,1fr)",gap:10,marginBottom:20}}>
      {[
        {label:"Overall Avg",value:overallAvg.toFixed(2)+" ",color:B.gold},
        {label:"Latest Month",value:latest?latest.avg.toFixed(2)+" ":"",color:B.teal},
        {label:"MoM Change",value:delta!==null?`${trendIcon} ${Math.abs(delta).toFixed(2)}`:"",color:trendColor},
        {label:"Total Reviews",value:totalReviews.toLocaleString(),color:B.cream},
      ].map(s=>(
        <div key={s.label} style={{background:B.navy,border:`1px solid ${B.borderMid}`,borderRadius:10,padding:"12px",textAlign:"center"}}>
          <div style={{...lbl,marginBottom:5}}>{s.label}</div>
          <div style={{fontSize:18,fontWeight:700,color:s.color,fontFamily:F.heading}}>{s.value}</div>
        </div>
      ))}
    </div>

    {/* Chart */}
    <div style={{background:B.navy,border:`1px solid ${B.borderMid}`,borderRadius:12,padding:"18px",marginBottom:20}}>
      <div style={{...lbl,marginBottom:14,color:B.teal}}>Monthly Rating Trend</div>
      <ResponsiveContainer width="100%" height={200}>
        <LineChart data={normTrend} margin={{top:5,right:20,left:-20,bottom:5}}>
          <CartesianGrid strokeDasharray="3 3" stroke={B.borderMid}/>
          <XAxis dataKey="label" tick={{fill:B.dimText,fontSize:10}}/>
          <YAxis domain={[1,5]} ticks={[1,2,3,4,5]} tick={{fill:B.dimText,fontSize:10}}/>
          <Tooltip content={<ChartTooltip/>}/>
          <ReferenceLine y={4.0} stroke={B.amber} strokeDasharray="4 4" strokeOpacity={0.6} label={{value:"4.0",fill:B.amber,fontSize:9,position:"right"}}/>
          <ReferenceLine y={4.5} stroke={B.green} strokeDasharray="4 4" strokeOpacity={0.5} label={{value:"4.5",fill:B.green,fontSize:9,position:"right"}}/>
          <Line type="monotone" dataKey="avg" stroke={B.teal} strokeWidth={2.5} dot={{fill:B.teal,r:4}} activeDot={{r:6,fill:B.gold}}/>
        </LineChart>
      </ResponsiveContainer>
      <div style={{display:"flex",gap:14,marginTop:8,justifyContent:"center"}}>
        <span style={{fontSize:9,color:B.green}}>  4.5 top tier</span>
        <span style={{fontSize:9,color:B.amber}}>  4.0 critical floor</span>
      </div>
    </div>

    {/* Monthly Table */}
    <div style={{background:B.navy,border:`1px solid ${B.borderMid}`,borderRadius:12,padding:"16px",marginBottom:20,overflowX:"auto"}}>
      <div style={{...lbl,marginBottom:12,color:B.dimText}}>Month by Month</div>
      <table style={{width:"100%",borderCollapse:"collapse",fontSize:12}}>
        <thead><tr>{["Month","Avg Rating","Reviews","vs Prior"].map(h=>(
          <th key={h} style={{...lbl,padding:"6px 10px",textAlign:"left",borderBottom:`1px solid ${B.borderMid}`}}>{h}</th>
        ))}</tr></thead>
        <tbody>{normTrend.map((m,i)=>{
          const d=i>0?m.avg-normTrend[i-1].avg:null;
          const dc=d===null?B.dimText:d>0?B.green:d<0?B.red:B.dimText;
          return(<tr key={m.month} style={{borderBottom:`1px solid ${B.borderMid}33`}}>
            <td style={{padding:"8px 10px",color:B.cream,fontFamily:F.heading,fontWeight:600}}>{m.label}</td>
            <td style={{padding:"8px 10px"}}><div style={{display:"flex",alignItems:"center",gap:8}}><span style={{color:B.gold,fontWeight:700,fontFamily:F.heading}}>{m.avg.toFixed(2)}</span><Stars rating={m.avg} size={12}/></div></td>
            <td style={{padding:"8px 10px",color:B.mutedText}}>{m.count}</td>
            <td style={{padding:"8px 10px",color:dc,fontWeight:700,fontFamily:F.heading}}>{d===null?"":d>0?` ${d.toFixed(2)}`:d<0?` ${Math.abs(d).toFixed(2)}`:" flat"}</td>
          </tr>);
        })}</tbody>
      </table>
    </div>

    {/* AI Analysis */}
    <div style={{background:`linear-gradient(135deg,#110820,#0a1a2e)`,border:`1px solid ${B.purple}55`,borderRadius:12,padding:"18px"}}>
      <div style={{display:"flex",alignItems:"center",justifyContent:"space-between",marginBottom:analysis?16:0}}>
        <div style={{display:"flex",alignItems:"center",gap:10}}>
          <span style={{fontSize:20}}></span>
          <div>
            <div style={{fontSize:12,fontWeight:700,color:B.purple,fontFamily:F.heading}}>AI Theme Analysis</div>
            <div style={{fontSize:10,color:B.mutedText}}>Identify what's driving your score</div>
          </div>
        </div>
        {!loading&&<button onClick={analyze} style={{padding:"8px 16px",borderRadius:7,border:"none",background:`linear-gradient(135deg,${B.purple},#6b3fd4)`,color:"#fff",fontSize:11,fontFamily:F.heading,fontWeight:700,cursor:"pointer"}}>{done?" Re-analyze":" Analyze"}</button>}
        {loading&&<div style={{fontSize:11,color:B.purple}}> Analyzing...</div>}
      </div>
      {err&&<div style={{fontSize:11,color:B.red,marginTop:10}}>{err}</div>}
      {analysis&&(<div>
        <div style={{display:"grid",gridTemplateColumns:"72px 1fr",gap:14,marginBottom:16,alignItems:"center"}}>
          <div style={{textAlign:"center"}}>
            <div style={{fontSize:38,fontWeight:700,color:analysis.healthScore>=7?B.green:analysis.healthScore>=5?B.amber:B.red,fontFamily:F.heading,lineHeight:1}}>{analysis.healthScore}</div>
            <div style={{fontSize:8,color:B.dimText,letterSpacing:"0.1em",textTransform:"uppercase",marginTop:3}}>/10 health</div>
          </div>
          <div>
            <div style={{display:"flex",gap:8,marginBottom:7,alignItems:"center"}}>
              <span style={{fontSize:9,fontFamily:F.heading,fontWeight:700,letterSpacing:"0.1em",textTransform:"uppercase",background:`${analysis.trend==="improving"?B.green:analysis.trend==="declining"?B.red:B.amber}22`,border:`1px solid ${analysis.trend==="improving"?B.green:analysis.trend==="declining"?B.red:B.amber}55`,borderRadius:4,padding:"2px 7px",color:analysis.trend==="improving"?B.green:analysis.trend==="declining"?B.red:B.amber}}>{analysis.trend?.toUpperCase()}</span>
            </div>
            <p style={{fontSize:11,color:B.cream,lineHeight:1.7}}>{analysis.summary}</p>
          </div>
        </div>
        <div style={{background:`${B.purple}15`,border:`1px solid ${B.purple}44`,borderLeft:`3px solid ${B.purple}`,borderRadius:8,padding:"10px 12px",marginBottom:14}}>
          <div style={{...lbl,color:B.purple,marginBottom:5}}> Biggest Opportunity</div>
          <p style={{fontSize:11,color:B.cream,lineHeight:1.7}}>{analysis.biggestOpportunity}</p>
        </div>
        <div style={{display:"grid",gridTemplateColumns:"1fr 1fr",gap:12,marginBottom:14}}>
          <div style={{background:`${B.green}0d`,border:`1px solid ${B.green}33`,borderRadius:10,padding:"12px"}}>
            <div style={{...lbl,color:B.green,marginBottom:10}}> What's Working</div>
            {(analysis.positiveThemes||[]).map((t,i)=>(<div key={i} style={{marginBottom:8}}>
              <div style={{display:"flex",justifyContent:"space-between",alignItems:"center",marginBottom:2}}>
                <span style={{fontSize:11,fontWeight:700,color:B.green,fontFamily:F.heading}}>{t.theme}</span>
                <span style={{fontSize:8,color:freqColor[t.frequency]||B.dimText,background:B.borderMid,borderRadius:3,padding:"1px 5px",textTransform:"uppercase"}}>{t.frequency}</span>
              </div>
              <p style={{fontSize:10,color:B.mutedText,lineHeight:1.6}}>{t.description}</p>
            </div>))}
          </div>
          <div style={{background:`${B.red}0d`,border:`1px solid ${B.red}33`,borderRadius:10,padding:"12px"}}>
            <div style={{...lbl,color:B.red,marginBottom:10}}> Friction Points</div>
            {(analysis.negativeThemes||[]).map((t,i)=>(<div key={i} style={{marginBottom:8}}>
              <div style={{display:"flex",justifyContent:"space-between",alignItems:"center",marginBottom:2}}>
                <span style={{fontSize:11,fontWeight:700,color:B.red,fontFamily:F.heading}}>{t.theme}</span>
                <span style={{fontSize:8,color:freqColor[t.frequency]||B.dimText,background:B.borderMid,borderRadius:3,padding:"1px 5px",textTransform:"uppercase"}}>{t.frequency}</span>
              </div>
              <p style={{fontSize:10,color:B.mutedText,lineHeight:1.6}}>{t.description}</p>
            </div>))}
          </div>
        </div>
        <div style={{background:`${B.amber}0d`,border:`1px solid ${B.amber}33`,borderRadius:10,padding:"12px",marginBottom:12}}>
          <div style={{...lbl,color:B.amber,marginBottom:8}}> Quick Wins</div>
          {(analysis.quickWins||[]).map((w,i)=>(<div key={i} style={{display:"flex",gap:8,marginBottom:8}}>
            <span style={{color:B.amber,fontWeight:700,fontSize:12,flexShrink:0}}>{i+1}.</span>
            <p style={{fontSize:11,color:B.cream,lineHeight:1.65}}>{w}</p>
          </div>))}
        </div>
        <div style={{background:`${B.teal}0d`,border:`1px solid ${B.teal}33`,borderRadius:10,padding:"12px"}}>
          <div style={{...lbl,color:B.teal,marginBottom:6}}> Response Strategy</div>
          <p style={{fontSize:11,color:B.cream,lineHeight:1.7}}>{analysis.responseStrategy}</p>
        </div>
        <p style={{fontSize:9,color:B.dimText,textAlign:"center",marginTop:12}}>Analysis powered by Claude AI  Based on Birchstone review data</p>
      </div>)}
    </div>
  </div>);
}

//  Main App 
const STORAGE_KEY = "birchstone-reviews-v1";

export default function App(){
  const[mainTab,setMainTab]=useState("dashboard"); // calculator | dashboard
  const[locationData,setLocationData]=useState(null); // null = not loaded yet
  const[selectedLoc,setSelectedLoc]=useState("");
  const[showUpload,setShowUpload]=useState(false);
  const[saveStatus,setSaveStatus]=useState("");
  const[lastUpdated,setLastUpdated]=useState(null);

  // Merge BIRCHSTONE_DATA with any stored data on mount
  useEffect(()=>{
    (async()=>{
      try{
        const result=await window.storage.get(STORAGE_KEY);
        if(result?.value){
          const saved=JSON.parse(result.value);
          // Merge: storage wins for months it has, fill gaps from base data
          const merged={...BIRCHSTONE_DATA};
          Object.keys(saved.locationData||{}).forEach(loc=>{
            if(!merged[loc])merged[loc]=saved.locationData[loc];
            else{
              // Merge by month key - stored data wins (it's newer uploads)
              const byMonth={};
              merged[loc].forEach(m=>{byMonth[m.m||m.month]=m;});
              (saved.locationData[loc]||[]).forEach(m=>{byMonth[m.m||m.month]=m;});
              merged[loc]=Object.values(byMonth).sort((a,b)=>(a.m||a.month).localeCompare(b.m||b.month));
            }
          });
          setLocationData(merged);
          setSelectedLoc(saved.selectedLoc||Object.keys(merged).sort()[0]||"");
          setLastUpdated(saved.lastUpdated||null);
        }else{
          setLocationData(BIRCHSTONE_DATA);
          setSelectedLoc(Object.keys(BIRCHSTONE_DATA).sort()[0]||"");
        }
      }catch{
        setLocationData(BIRCHSTONE_DATA);
        setSelectedLoc(Object.keys(BIRCHSTONE_DATA).sort()[0]||"");
      }
    })();
  },[]);

  const save=async(data,loc,updated)=>{
    setSaveStatus("saving");
    try{
      // Only store data that DIFFERS from base BIRCHSTONE_DATA (new uploads)
      const extraData={};
      Object.keys(data).forEach(location=>{
        if(!BIRCHSTONE_DATA[location]){extraData[location]=data[location];return;}
        // Find months not in base data
        const baseMonths=new Set(BIRCHSTONE_DATA[location].map(m=>m.m||m.month));
        const extra=(data[location]||[]).filter(m=>!baseMonths.has(m.m||m.month));
        if(extra.length)extraData[location]=extra;
      });
      await window.storage.set(STORAGE_KEY,JSON.stringify({locationData:extraData,selectedLoc:loc,lastUpdated:updated}));
      setSaveStatus("saved");setTimeout(()=>setSaveStatus(""),2500);
    }catch{setSaveStatus("error");}
  };

  const handleImport=(newData,mode)=>{
    const base=mode==="replace"?{}:{...locationData};
    Object.keys(newData).forEach(loc=>{
      if(!base[loc]){base[loc]=newData[loc];return;}
      const byMonth={};
      base[loc].forEach(m=>{byMonth[m.m||m.month]=m;});
      newData[loc].forEach(m=>{byMonth[m.m||m.month]=m;});
      base[loc]=Object.values(byMonth).sort((a,b)=>(a.m||a.month).localeCompare(b.m||b.month));
    });
    const updated=new Date().toLocaleString();
    setLocationData(base);
    if(!base[selectedLoc])setSelectedLoc(Object.keys(base).sort()[0]||"");
    setLastUpdated(updated);
    setShowUpload(false);
    save(base,selectedLoc,updated);
  };

  const locations=locationData?Object.keys(locationData).sort():[];

  return(<>
    <style>{`
      @import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@400;500;600&family=Raleway:wght@400;600;700&display=swap');
      *{box-sizing:border-box;margin:0;padding:0;}
      input[type=number]::-webkit-inner-spin-button{opacity:0.3;}
      input:focus,textarea:focus{border-color:${B.teal}!important;box-shadow:0 0 0 2px ${B.teal}22;outline:none;}
      button{transition:all 0.15s;} button:hover:not(:disabled){opacity:0.85;}
      select{cursor:pointer;}
      ::-webkit-scrollbar{width:5px;} ::-webkit-scrollbar-track{background:${B.darkest};} ::-webkit-scrollbar-thumb{background:${B.borderMid};border-radius:3px;}
    `}</style>

    <div style={{minHeight:"100vh",background:`linear-gradient(160deg,${B.darkest} 0%,#091e2d 55%,${B.darkest} 100%)`,fontFamily:F.body,color:B.cream}}>

      {/* Header */}
      <div style={{background:`linear-gradient(90deg,${B.navy},#004068)`,borderBottom:`1px solid ${B.border}`,padding:"18px 24px",position:"relative"}}>
        <div style={{position:"absolute",top:0,left:0,right:0,height:3,background:`linear-gradient(90deg,${B.teal},${B.purple},${B.amber},${B.teal})`}}/>
        <div style={{display:"flex",alignItems:"center",justifyContent:"space-between",gap:12}}>
          <div style={{display:"flex",alignItems:"center",gap:14}}>
            <div style={{width:40,height:40,background:"#000",borderRadius:8,display:"flex",alignItems:"center",justifyContent:"center",flexShrink:0}}>
              <span style={{color:"#fff",fontSize:22,fontFamily:F.heading,fontWeight:700}}>B</span>
            </div>
            <div>
              <div style={{fontSize:9,letterSpacing:"0.3em",color:B.teal,textTransform:"uppercase",fontFamily:F.heading,fontWeight:600,marginBottom:2}}>Birchstone Residential</div>
              <h1 style={{fontSize:18,fontWeight:"normal",color:B.cream,fontFamily:F.heading}}>
                Reputation <span style={{color:B.teal}}>Intelligence Suite</span>
              </h1>
            </div>
          </div>
          <div style={{display:"flex",alignItems:"center",gap:8}}>
            {saveStatus==="saving"&&<span style={{fontSize:10,color:B.mutedText}}> Saving...</span>}
            {saveStatus==="saved"&&<span style={{fontSize:10,color:B.green}}> Saved</span>}
            {saveStatus==="error"&&<span style={{fontSize:10,color:B.red}}> Save failed</span>}
          </div>
        </div>
        {/* Main nav tabs */}
        <div style={{display:"flex",gap:8,marginTop:14}}>
          {[["dashboard"," Review Dashboard"],["calculator"," Score Calculator"]].map(([id,label])=>(
            <button key={id} onClick={()=>setMainTab(id)} style={{padding:"8px 18px",borderRadius:8,border:`1px solid ${mainTab===id?B.teal:B.borderMid}`,background:mainTab===id?`${B.teal}22`:"none",color:mainTab===id?B.teal:B.mutedText,fontSize:12,fontFamily:F.heading,fontWeight:700,cursor:"pointer"}}>{label}</button>
          ))}
        </div>
      </div>

      <div style={{padding:"20px 18px 48px",maxWidth:820,margin:"0 auto"}}>

        {/*  DASHBOARD TAB  */}
        {mainTab==="dashboard"&&(
          <div>
            {!locationData?(
              <div style={{textAlign:"center",padding:"60px 0",color:B.mutedText}}>Loading data...</div>
            ):(
              <div>
                {/* Location selector + controls */}
                <div style={{display:"flex",alignItems:"center",gap:12,marginBottom:20,flexWrap:"wrap"}}>
                  <div style={{...lbl,flexShrink:0}}>Location</div>
                  <select value={selectedLoc} onChange={e=>{setSelectedLoc(e.target.value);}}
                    style={{flex:1,minWidth:200,background:B.navy,border:`1px solid ${B.teal}55`,borderRadius:8,padding:"10px 14px",color:B.cream,fontSize:14,fontFamily:F.heading,fontWeight:700,outline:"none",colorScheme:"dark"}}>
                    {locations.map(l=><option key={l} value={l}>{l}</option>)}
                  </select>
                  <div style={{display:"flex",gap:8,flexShrink:0}}>
                    <button onClick={()=>setShowUpload(u=>!u)} style={{padding:"8px 14px",borderRadius:7,border:`1px solid ${showUpload?B.teal:B.borderMid}`,background:showUpload?`${B.teal}18`:"none",color:showUpload?B.teal:B.mutedText,fontSize:11,fontFamily:F.heading,fontWeight:700,cursor:"pointer"}}>
                      {showUpload?" Cancel":"+ Add Data"}
                    </button>
                  </div>
                </div>
                {lastUpdated&&<div style={{fontSize:9,color:B.dimText,marginBottom:14}}>Last upload: {lastUpdated}  {locations.length} locations  Aug 2025Jan 2026 pre-loaded</div>}

                {showUpload&&<UploadPanel onData={handleImport} onClose={()=>setShowUpload(false)}/>}

                {selectedLoc&&locationData[selectedLoc]&&(
                  <LocationDashboard key={selectedLoc} location={selectedLoc} trend={locationData[selectedLoc]}/>
                )}
              </div>
            )}
          </div>
        )}

        {/*  CALCULATOR TAB  */}
        {mainTab==="calculator"&&<CalculatorSection/>}

      </div>
    </div>
  </>);
}
